// Spending Manager for:
// 1. AddSellers
//   - Increase `seller_count` 
// 2. CollectSellers
//   - Decrease `seller_count`
// 3. SpendManager
//   - Update `reserve_raise`, `total_penalty` of Treasury
validator(
  // ValidatorHash of Treasury Validator
  treasury_hash: ValidatorHash,
) {
  fn validate_manager_spending(
    manager_in_datum: ManagerDatum,
    redeemer: ManagerRedeemer,
    context: ScriptContext,
  ) {
    expect ScriptContext { purpose: Spend(out_ref), transaction } = context
    let Transaction {
      mint,
      reference_inputs,
      validity_range,
      inputs,
      outputs,
      redeemers,
      ..
    } = transaction
    let mint_value = value.from_minted_value(mint)
    let ManagerDatum { factory_policy_id, .. } = manager_in_datum
    when redeemer is {
      // Collect Manager Transaction note:
      //   1 Manager Token needs to be burned (trigger Factory Minting)
      //   1 Manager Input needs to be spent
      //   1 Treasury Input needs to be spent
      //   1 Treasury Output needs to be paid
      SpendManager ->
        // Delegate validation logic to the Treasury Validator.
        validation.has_spend(
          inputs: inputs,
          auth_policy_id: factory_policy_id,
          auth_asset_name: treasury_auth_an,
          validator_hash: treasury_hash,
          redeemers: redeemers,
          expected_redeemer: CollectManager,
        )
      _ -> {
        expect Some(manager_input) =
          list.find(
            inputs,
            fn(input) {
              let Input { output_reference, .. } = input
              output_reference == out_ref
            },
          )
        // Extract some necessary data.
        expect Input {
          output: Output {
            address: Address {
              payment_credential: ScriptCredential(manager_hash),
              ..
            },
            value: manager_in_value,
            ..
          },
          ..
        } = manager_input
        expect [treasury_ref_input] =
          validation.get_treasury_ref_inputs(
            ref_inputs: reference_inputs,
            factory_policy_id: factory_policy_id,
            treasury_hash: treasury_hash,
          )
        expect Input {
          output: Output { datum: InlineDatum(raw_treasury_datum), .. },
          ..
        } = treasury_ref_input
        expect treasury_datum: TreasuryDatum = raw_treasury_datum
        // Assertion:
        // - Manager Input is legitimate
        // - Only 1 Manager Input
        expect and {
            value.quantity_of(
              manager_in_value,
              factory_policy_id,
              manager_auth_an,
            ) == 1,
            list.filter(
              inputs,
              fn(input) {
                let Input {
                  output: Output {
                    address: Address { payment_credential, .. },
                    ..
                  },
                  ..
                } = input
                payment_credential == ScriptCredential(manager_hash)
              },
            ) == [manager_input],
          }
        validate_manage_seller(
          outputs: outputs,
          inputs: inputs,
          factory_policy_id: factory_policy_id,
          manager_in_datum: manager_in_datum,
          manager_in_value: manager_in_value,
          validity_range: validity_range,
          mint_value: mint_value,
          treasury_datum: treasury_datum,
          redeemer: redeemer,
        )
      }
    }
  }
}

use aiken/builtin
use aiken/collection/dict
use aiken/collection/list
use aiken/crypto.{ScriptHash}
use cardano/assets
use cardano/script_context.{ScriptContext, Withdrawing}
use cardano/transaction.{Transaction}

/// The stake validator is the indirection used to allow orders to safely execute in a scoop
/// Each order can very efficiently check for this stake script, and then this stake script can look for the pool token
/// just once, even if we have 35 orders.
///
/// It's parameterized by the pool_script_hash, so it knows what pool token to look for.
validator stake(pool_script_hash: ScriptHash) {
  else(ctx: ScriptContext) {
    when ctx.info is {
      Withdrawing(_) -> {
        // We can assume that the pool output is the first output, because outputs aren't reordered by the ledger,
        // and nothing is harmed by making this assumption;
        // Additionally, if we find an output that *isn't* the pool output, the worst that will happen is the
        // transaction will fail, because it doesn't have the unique pool identifier
        // We should also ensure when creating the pool that there are no withdrawals, to prevent
        // spending orders as if they were a scoop while minting a pool (which would satisfy the pool NFT being on the output)
        // That way we can also avoid having to check the mint here, whcih saves a good bit of execution units
        expect Some(pool_output) = list.head(ctx.transaction.outputs)
        // Lookup anything with the policy ID of the pool script hash we're parameterized by
        let pool_tokens = assets.tokens(pool_output.value, pool_script_hash)
        let pool_tokens_list = dict.to_pairs(pool_tokens)
        // And then check that it's specifically the pool NFT by taking advantage of the CIP-68 label
        let is_pool_nft =
          fn(
            /// Note: we don't allow this address to be delegated to a pool, only withdrawals (which will always be zero)
            /// because a very subtle attack vector would be to register the address, delegate to a pool,
            /// and break any offchain code that was assuming the withdraw would be zero
            /// because most off-chain code won't have access to the rewards calculation!
            kvp: Pair<ByteArray, Int>,
          ) {
            // we use the 2nd byte here, because the first byte is zero as a "parenthesis"
            builtin.index_bytearray(kvp.1st, 1) == 0xd
          }
        // If any of the tokens are the pool NFT (needed because there are likely other tradable assets on the pool)
        // then we can be sure the pool validator itself will run, and that will enforce all of the important security constraints
        list.any(pool_tokens_list, is_pool_nft)
      }
      _ -> False
    }
  }
}

use aiken/list
use aiken/transaction.{Input, Mint, Output, ScriptContext, Transaction}
use aiken/transaction/value
use stableswap/types.{Asset}

validator(nft_asset: Asset) {
  fn validate_lp_minting(_redeemer: Data, context: ScriptContext) -> Bool {
    let ScriptContext { transaction, purpose } = context
    expect Mint(own_policy_id) = purpose
    let Transaction { inputs, outputs, mint, .. } = transaction
    let flatten_mint_value = value.flatten(value.from_minted_value(mint))
    let Asset { policy_id: nft_policy_id, asset_name: nft_asset_name } =
      nft_asset

    // Verify there is only single NFT in the Inputs
    expect [_] =
      list.filter(
        inputs,
        fn(i) {
          let Input { output, .. } = i
          let Output { value: v, .. } = output
          value.quantity_of(v, nft_policy_id, nft_asset_name) == 1
        },
      )

    // Verify there is only single NFT in the Outputs
    expect [_] =
      list.filter(
        outputs,
        fn(o) {
          let Output { value: v, .. } = o
          value.quantity_of(v, nft_policy_id, nft_asset_name) == 1
        },
      )

    // Verify only LP Asset is minted in this transaction and its name must be equals NFT name
    expect [(pid, tn, _am)] = flatten_mint_value
    pid == own_policy_id && tn == nft_asset_name
  }
}

