            treasury_hash,
          )
        let Transaction { datums, .. } = transaction
        and {
          // before discovery phase
          end_valid_time_range < start_time,
          // prevent updating the LBE when it has been cancelled.
          is_cancelled == False,
          // Authorize by owner in input treasury datum 
          validation.validate_authorize_by_owner(
            owner_payment_credential: owner_payment_credential,
            extra_signatories: extra_signatories,
            inputs: inputs,
          ),
          // create new one
          validate_creating_treasury_out(
            treasury_out: treasury_output,
            base_asset: base_asset,
            raise_asset: raise_asset,
            manager_hash: manager_hash,
            seller_hash: seller_hash,
            order_hash: order_hash,
            factory_policy_id: factory_policy_id,
            end_valid_time_range: end_valid_time_range,
            datums: datums,
            extra_signatories: extra_signatories,
            inputs: inputs,
          ),
          // mint nothing
          mint_value == value.zero(),
        }
      }
      CollectManager ->
        validation.validate_collect_manager(
          inputs: inputs,
          outputs: outputs,
          treasury_in_value: treasury_in_value,
          treasury_in_datum: treasury_in_datum,
          mint_value: mint_value,
          factory_policy_id: factory_policy_id,
          manager_hash: manager_hash,
          treasury_hash: treasury_hash,
        )
      CollectOrders ->
        validate_collect_orders(
          inputs: inputs,
          outputs: outputs,
          treasury_in_datum: treasury_in_datum,
          treasury_in_value: treasury_in_value,
          mint_value: mint_value,
          treasury_hash: treasury_hash,
        )
      CloseEvent -> {
        // forward logic to factory with case 2 factory inputs
        expect [_, _] =
          list.filter(
            inputs,
            fn(input) {
              let Input {
                output: Output {
                  address: Address { payment_credential, .. },
                  value: out_value,
                  ..
                },
                ..
              } = input
              and {
                payment_credential == ScriptCredential(factory_policy_id),
                value.quantity_of(out_value, factory_policy_id, factory_auth_an) == 1,
              }
            },
          )
        True
      }
      CreateAmmPool -> {
        let treasury_output =
          validation.get_treasury_output(
            outputs,
            factory_policy_id,
            treasury_hash,
          )
        let pool_output = validation.get_amm_pool_output(outputs)
        expect Output { datum: InlineDatum(pool_raw_datum), .. } = pool_output
        expect amm_pool_datum: PoolDatum = pool_raw_datum
        let receiver_output = builtin.head_list(outputs)
        validate_create_dex_pool(
          treasury_in_datum: treasury_in_datum,
          treasury_output: treasury_output,
          mint_value: mint_value,
          amm_pool_datum: amm_pool_datum,
          factory_policy_id: factory_policy_id,
          receiver_output: receiver_output,
        )
      }
      RedeemOrders ->
        when is_cancelled is {
          True ->
            validate_refund(
              inputs: inputs,
              outputs: outputs,
              treasury_in_datum: treasury_in_datum,
              treasury_in_value: treasury_in_value,
              mint_value: mint_value,
              treasury_hash: treasury_hash,
            )
          False ->
            validate_redeem(
              inputs: inputs,
              outputs: outputs,
              treasury_in_datum: treasury_in_datum,
              treasury_in_value: treasury_in_value,
              mint_value: mint_value,
              treasury_hash: treasury_hash,
            )
        }
      CancelLBE(reason) -> {
        // finding exactly 1 Legit Treasury Output
        let treasury_output =
          validation.get_treasury_output(
            outputs,
            factory_policy_id,
            treasury_hash,
          )
        validate_cancel_lbe(
          treasury_in_datum: treasury_in_datum,
          treasury_in_value: treasury_in_value,
          treasury_output: treasury_output,
          ref_inputs: reference_inputs,
          inputs: inputs,
          extra_signatories: extra_signatories,
          end_valid_time_range: end_valid_time_range,
          cancel_reason: reason,
          mint_value: mint_value,
        )
      }
    }
  }
}

use aiken/list
use aiken/transaction.{
  InlineDatum, Input, Output, ScriptContext, Spend, Transaction,
}
use aiken/transaction/credential.{Address, ScriptCredential}
use aiken/transaction/value
use lb_v2/manager_validation.{validate_manage_seller}
use lb_v2/types.{
  CollectManager, ManagerDatum, ManagerRedeemer, SpendManager, TreasuryDatum,
  ValidatorHash,
}
use lb_v2/utils.{manager_auth_an, treasury_auth_an}
use lb_v2/validation

// Spending Manager for:
// 1. AddSellers
//   - Increase `seller_count` 
// 2. CollectSellers
//   - Decrease `seller_count`
// 3. SpendManager
//   - Update `reserve_raise`, `total_penalty` of Treasury
validator(
  // ValidatorHash of Treasury Validator
  treasury_hash: ValidatorHash,
) {
  fn validate_manager_spending(
    manager_in_datum: ManagerDatum,
    redeemer: ManagerRedeemer,
    context: ScriptContext,
  ) {
    expect ScriptContext { purpose: Spend(out_ref), transaction } = context
    let Transaction {
      mint,
      reference_inputs,
      validity_range,
      inputs,
      outputs,
      redeemers,
      ..
    } = transaction
    let mint_value = value.from_minted_value(mint)
    let ManagerDatum { factory_policy_id, .. } = manager_in_datum
    when redeemer is {
      // Collect Manager Transaction note:
      //   1 Manager Token needs to be burned (trigger Factory Minting)
      //   1 Manager Input needs to be spent
      //   1 Treasury Input needs to be spent
      //   1 Treasury Output needs to be paid
      SpendManager ->
        // Delegate validation logic to the Treasury Validator.
        validation.has_spend(
          inputs: inputs,
          auth_policy_id: factory_policy_id,
          auth_asset_name: treasury_auth_an,
          validator_hash: treasury_hash,
          redeemers: redeemers,
          expected_redeemer: CollectManager,
        )
      _ -> {
        expect Some(manager_input) =
          list.find(
            inputs,
            fn(input) {
              let Input { output_reference, .. } = input
              output_reference == out_ref
            },
          )
        // Extract some necessary data.
        expect Input {
          output: Output {
            address: Address {
              payment_credential: ScriptCredential(manager_hash),
              ..
            },
            value: manager_in_value,
            ..
          },
          ..
        } = manager_input
        expect [treasury_ref_input] =
          validation.get_treasury_ref_inputs(
            ref_inputs: reference_inputs,
            factory_policy_id: factory_policy_id,
            treasury_hash: treasury_hash,
          )
        expect Input {
          output: Output { datum: InlineDatum(raw_treasury_datum), .. },
          ..
        } = treasury_ref_input
        expect treasury_datum: TreasuryDatum = raw_treasury_datum
        // Assertion:
        // - Manager Input is legitimate
        // - Only 1 Manager Input
        expect and {
            value.quantity_of(
              manager_in_value,
              factory_policy_id,
              manager_auth_an,
            ) != 1, // Inverted condition
            list.filter(
              inputs,
              fn(input) {
                let Input {
                  output: Output {
                    address: Address { payment_credential, .. },
                    ..
                  },
                  ..
                } = input
                payment_credential == ScriptCredential(manager_hash)
              },
            ) == [manager_input],
          }
        validate_manage_seller(
          outputs: outputs,
          inputs: inputs,
          factory_policy_id: factory_policy_id,
          manager_in_datum: manager_in_datum,
          manager_in_value: manager_in_value,
          validity_range: validity_range,
          mint_value: mint_value,
          treasury_datum: treasury_datum,
          redeemer: redeemer,
        )
      }
    }
  }
}

