use aiken/dict
use aiken/list
use aiken/transaction.{
  Input, Output, ScriptContext, Spend, Transaction, WithdrawFrom,
}
use aiken/transaction/credential.{
  Address, ScriptCredential, StakeCredential, VerificationKeyCredential,
}
use stableswap/types.{
  ApplyOrder, CancelOrder, OrderBatchingRedeemer, OrderDatum, OrderRedeemer,
  ValidatorHash,
}
use stableswap/utils

validator(
  // the Stake Credential of Order Batching Validator
  stake_credential: StakeCredential,
) {
  fn validate_order(
    raw_datum: Data,
    redeemer: OrderRedeemer,
    context: ScriptContext,
  ) -> Bool {
    let ScriptContext { transaction, purpose } = context
    expect Spend(_) = purpose
    when redeemer is {
      ApplyOrder -> {
        let Transaction { withdrawals, .. } = transaction
        // validate that an Order can be spent if there's a `Order Batching` validator in the `withdrawals`
        dict.has_key(withdrawals, stake_credential)
      }
      CancelOrder -> {
        let Transaction { inputs, extra_signatories, .. } = transaction
        expect order_datum: OrderDatum = raw_datum
        let OrderDatum {
          sender: Address { payment_credential: owner_payment_credential, .. },
          ..
        } = order_datum
        // validate that the transaction has _sender_'s signature or _sender_ script UTxO in the Transaction Inputs
        when owner_payment_credential is {
          VerificationKeyCredential(owner_pkh) ->
            // Verify transaction must be signed by Owner
            list.has(extra_signatories, owner_pkh)
          ScriptCredential(_) ->
            // In case owner is script address, this script will require at least 1 owner UTxO in input
            // If owner UTxO has enough condition to unlock itself, it can unlock this script as well
            list.any(
              inputs,
              fn(input) {
                let Input { output, .. } = input
                let Output { address: out_address, .. } = output
                let Address { payment_credential: out_payment_credential, .. } =
                  out_address
                out_payment_credential == owner_payment_credential
              },
            )
        }
      }
    }
  }
}

validator(
  // the hash of Liquidity Pool Script
  pool_hash: ValidatorHash,
) {
  fn validate_order_spending_in_batching(
    redeemer: OrderBatchingRedeemer,
    context: ScriptContext,
  ) -> Bool {
    expect ScriptContext { transaction, purpose: WithdrawFrom(_) } = context
    let OrderBatchingRedeemer { pool_input_index } = redeemer

    let Transaction { inputs, .. } = transaction
    let Input {
      output: Output { address: Address { payment_credential, .. }, .. },
      ..
    } = utils.list_at_index(inputs, pool_input_index)
    expect ScriptCredential(hash) = payment_credential
    // validate that there's a Pool Input which have Address's Payment Credential matching with `pool_hash`
    pool_hash == hash
  }
}

use aiken/builtin
use aiken/collection/dict
use aiken/collection/list
use aiken/crypto.{blake2b_256}
use aiken/primitive/bytearray
use cardano/address.{Address, PaymentCredential, Script}
use cardano/assets.{PolicyId}
use cardano/transaction.{
  InlineDatum, Input, Output, OutputReference, Transaction,
} as tx

pub type Action {
  Mint(Int)
  Burn
}

pub type SpendTokenName =
  ByteArray

validator redeem(creator: ByteArray) {
  spend(
    // Each spend input checks for a token name matching the datum being burned
    datum: Option<SpendTokenName>,
    _r: Data,
    own_ref: OutputReference,
    transaction: Transaction,
  ) {
    let Transaction { inputs, mint, .. } = transaction

    expect Some(own_input) =
      list.find(inputs, fn(input) { input.output_reference == own_ref })

    let Input {
      output: Output { address: Address { payment_credential, .. }, .. },
      ..
    } = own_input

    expect Script(own_validator_hash) = payment_credential
    expect Some(datum) = datum

    (
      mint
        |> assets.quantity_of(own_validator_hash, datum)
    ) == -1
  }

  mint(rdmr: Action, policy_id: PolicyId, transaction: Transaction) {
    let Transaction { inputs, mint, extra_signatories, outputs, .. } =
      transaction

    let minted_assets =
      mint
        |> assets.tokens(policy_id)
        |> dict.to_pairs()

    when rdmr is {
      Mint(total) -> {
        expect [input, ..] = inputs

        // Base is created from serializing a utxo ref being spent. Thus this guarantees a unique base
        let base = builtin.serialise_data(input.output_reference)

        // Create a list of expected token names
        let expected_minted_token_names =
          create_expected_minted_nfts(base, total, [])

        // Check contract creator is a signer of this tx
        let signature_check =
          list.any(extra_signatories, fn(n) { creator == n })

        // Support multiple gift card creation by allowing a
        // 'number of tokens minted' == 'outputs with datum being token name'
        signature_check && check_mint_and_outputs(
          minted_assets,
          outputs,
          expected_minted_token_names,
          Script(policy_id),
        )
      }
      Burn ->
        list.all(
          minted_assets,
          fn(asset) {
            let Pair(_, amount) = asset
            amount == -1
          },
        )
    }
  }

  else(_) {
    fail
  }
}

fn insert(self: List<a>, e: a, compare: fn(a, a) -> Ordering) -> List<a> {
  when self is {
    [] -> [e]
    [x, ..xs] ->
      if compare(e, x) == Less {
        [e, ..self]
      } else {
        [x, ..insert(xs, e, compare)]
      }
  }
}

// Check each minted token name is in the expected list, has quantity of 1,
// and has a corresponding ouput with datum containing token name.
// Otherwise fail
fn check_mint_and_outputs(
  minted_assets: Pairs<ByteArray, Int>,
  outputs: List<Output>,
  expected_assets: List<ByteArray>,
  validator_cred: PaymentCredential,
) -> Bool {
  when minted_assets is {
    [] -> True
    [Pair(minted_asset_name, quantity), ..rest_assets] -> {
      expect
        list.any(
          expected_assets,
          fn(expected_asset) { expected_asset == minted_asset_name },
        )

      expect
        list.any(
          outputs,
          fn(output) {
            let Output { address, datum, .. } = output
            datum == InlineDatum(minted_asset_name) && address.payment_credential == validator_cred
          },
        )

      quantity == 1 && check_mint_and_outputs(
        rest_assets,
        outputs,
        expected_assets,
        validator_cred,
      )
    }
  }
}

fn create_expected_minted_nfts(
  base: ByteArray,
  counter: Int,
  accum: List<ByteArray>,
) -> List<ByteArray> {
  if counter == 0 {
    accum
  } else {
    let token_name = blake2b_256(bytearray.push(base, counter))

    let accum = [token_name, ..accum]

    create_expected_minted_nfts(base, counter - 1, accum)
  }
}

use aiken/hash.{Hash,Blake2b_224}
use aiken/transaction.{ScriptContext,Transaction,Input,Output} as tx
use aiken/transaction/credential.{Script,ScriptCredential,Inline}

use cardano_swaps/one_way_swap/types.{ 
  BeaconRedeemer,
  CreateOrCloseSwaps,
  SpendWithMint,
  SpendWithStake,
  UpdateSwaps,
  Swap,
  SwapDatum,
  SwapRedeemer
}
use cardano_swaps/common/utils.{trace_if_false} as common_utils
use cardano_swaps/one_way_swap/utils as utils

// The logic of `SpendWithMint` and `SpendWithStake` are identical except for how it expects the
// beacon_script to be executed: `SpendWithMint` expects a minting policy execution and
// `SpendWithStake` expects a staking script execution. The reason for this is that there are
// scenarios where swaps need to be updated but no beacons need to be minted/burned. Only one type
// of execution is ever needed in the transaction (you can also update swaps using `SpendWithMint`).
// The idea is that `SpendWithMint` is meant to pair with `CreateOrCloseSwaps` (this allows closing
// swaps and changing the swap's trading pair), and `SpendWithStake` is meant to pair with
// `UpdateSwaps` (this allows cheaply updating swaps when no minting/burning is needed).
validator() {
  fn swap_script(datum: SwapDatum, redeemer: SwapRedeemer, ctx: ScriptContext) -> Bool {
    when redeemer is {
      SpendWithMint -> {
        // 1) The address' staking credential must signal approval.
        // 2) The beacon script must be executed as a minting policy.

        // Get required information for script context.
        expect ScriptContext( 
          Transaction(inputs,_,_,_,mint,_,wtdr,_,sigs,..), 
          tx.Spend(input_ref)
        ) = ctx
        expect Some(Input(_, Output(addr,..))) = tx.find_input(inputs,input_ref)
        let SwapDatum(beacon_id,..) = datum

        // The address' staking credential must signal approval (Requirement 1).
        trace_if_false( 
          @"Staking credential did not approve", 
          common_utils.staking_credential_approves( addr, wtdr, sigs)
        ) &&

        // The beacon_script was not executed as a minting policy.
        trace_if_false(
          @"Beacon script not executed as minting policy",
          common_utils.has_beacon_script_minting_execution(beacon_id,mint)
        )
      }

      SpendWithStake -> {
        // 1) The address' staking credential must signal approval.
        // 2) The beacon script must be executed as a staking script.

        // Get required information for script context.
        expect ScriptContext( 
          Transaction(inputs,_,_,_,_,_,wtdr,_,sigs,..), 
          tx.Spend(input_ref)
        ) = ctx
        expect Some(Input(_, Output(addr,..))) = tx.find_input(inputs,input_ref)
        let SwapDatum(beacon_id,..) = datum

        // The address' staking credential must signal approval (Requirement 1).
        trace_if_false( 
          @"Staking credential did not approve", 
          common_utils.staking_credential_approves( addr, wtdr, sigs)
        ) &&

        // The beacon_script was not executed as a staking script.
        trace_if_false(
          @"Beacon script not executed as staking script",
          common_utils.has_beacon_script_staking_execution(beacon_id,wtdr)
        )
      }

      Swap -> {
        // Requirements:
        // 1) The input must contain the beacons for that trading pair.
        // 2) There must must be an output to this address with the proper value and inline
        //    SwapDatum:
        //      a) Must contain exactly 1 of each of the required beacons.
        //      b) The swap datum must be exactly the same as the input's except the prev_input
        //         must be == Some(input_ref).
        // 3) Offer taken * price <= Ask given
        // 4) Only the offer asset leaves and only the ask asset is deposited. Ada can always be 
        //    deposited.
        //
        // Requirement 1 guarantees that all invalid UTxOs belong to the address owner and that
        // swap inputs have a valid price:
        //      a) `swap_price` denominator > 0
        //      b) `swap_price` numerator > 0
        //
        // Requirement 2 guarantees that beacons from other trading pairs cannot be combined into
        // one output UTxO; all swaps must get their own swap UTxOs.
        //
        // Requirement 4 helps optimize beacon queries by keeping UTxO asset lists to the smallest
        // possible size.

        // Get required information for script context.
        expect ScriptContext( 
          Transaction(inputs,_,outputs,..), 
          tx.Spend(input_ref)
        ) = ctx
        expect Some(Input(_, Output(addr,val,..))) = tx.find_input(inputs,input_ref)

        // Extract out the information from this datum.
        let SwapDatum(
          beacon_id, 
          pair_beacon,
          offer_id,
          offer_name,
          offer_beacon,
          ask_id,
          ask_name,
          ask_beacon,
          swap_price,
          _ // prev_input is not needed.
        ) = datum

        // The following checks requirements 1,2,3,4. It has its own error messages.
        utils.valid_swap(
          input_ref,
          val, // Input's value.
          addr, // This address.
          beacon_id,
          pair_beacon,
          offer_id,
          offer_name,
          offer_beacon,
          ask_id,
          ask_name,
          ask_beacon,
          swap_price,
          outputs
        )
      }
    }
  }
}

// The logic for `CreateOrCloseSwaps` and `UpdateSwaps` are identical except for the fact that
// `CreateOrCloseSwaps` can mint/burn beacons due to being executed as a minting script. The ledger
// will prevent `UpdateSwaps` from minting since staking script executions cannot mint/burn tokens.
validator(dapp_hash: Hash<Blake2b_224, Script>) {
  fn beacon_script(redeemer: BeaconRedeemer, ctx: ScriptContext) -> Bool {
    when redeemer is {
      CreateOrCloseSwaps -> {
        // Requirements:
        // 1) Must be executed as a minting script.
        // 2) The beacons must go to an address protected by the DApp validator script.
        // 3) The beacons must go to an address using a valid staking credential.
        // 4) The UTxOs with the beacons must have the proper value:
        //      a) Exactly three kinds of beacons: pair beacon, offer beacon, and ask beacon.
        //      b) The beacons must correspond to the beacons in the datum.
        //      c) There must be exactly 1 of each beacon.
        //      d) No extraneous assets are in the UTxO.
        // 5) The beacons must be stored with the proper inline `SwapDatum`:
        //      a) `beacon_id` == this policy id.
        //      b) `pair_beacon` == `sha2_256(offer_id ++ offer_name ++ ask_id ++ ask_name)`
        //      c) `offer_id` == policy id of the offer asset.
        //      d) `offer_name` == asset name of the offer asset.
        //      e) `offer_beacon` == `sha2_256("01" ++ offer_id ++ offer_name)`.
        //      f) `ask_id` == policy id of the ask asset.
        //      g) `ask_name` == asset name of the ask asset.
        //      h) `ask_beacon` == `sha2_256("02" ++ ask_id ++ ask_name)`
        //      i) `swap_price` denominator > 0
        //      j) `swap_price` numerator > 0
        // 6) The offer asset and the ask asset must be different assets.
        // 7) Any unused beacons must be burned.

        // Get required information from the script context.
        let ScriptContext( Transaction(_,_,outputs,..), purpose) = ctx

        let policy_id = when purpose is {
          tx.Mint(policy_id) -> policy_id
          _ -> error @"Redeemer not used with minting execution"
        }

        utils.beacon_destination_check(
          dapp_hash,
          policy_id,
          outputs
        )
      }

      UpdateSwaps -> {
        // Requirements:
        // 1) Must be executed as a staking script.
        // 2) The beacons must go to an address protected by the DApp validator script.
        // 3) The beacons must go to an address using a valid staking credential.
        // 4) The UTxOs with the beacons must have the proper value:
        //      a) Exactly three kinds of beacons: pair beacon, offer beacon, and ask beacon.
        //      b) The beacons must correspond to the beacons in the datum.
        //      c) There must be exactly 1 of each beacon.
        //      d) No extraneous assets are in the UTxO.
        // 5) The beacons must be stored with the proper inline `SwapDatum`:
        //      a) `beacon_id` == this policy id.
        //      b) `pair_beacon` == `sha2_256(offer_id ++ offer_name ++ ask_id ++ ask_name)`
        //      c) `offer_id` == policy id of the offer asset.
        //      d) `offer_name` == asset name of the offer asset.
        //      e) `offer_beacon` == `sha2_256("01" ++ offer_id ++ offer_name)`.
        //      f) `ask_id` == policy id of the ask asset.
        //      g) `ask_name` == asset name of the ask asset.
        //      h) `ask_beacon` == `sha2_256("02" ++ ask_id ++ ask_name)`
        //      i) `swap_price` denominator > 0
        //      j) `swap_price` numerator > 0
        // 6) The offer asset and the ask asset must be different assets.

        // Get required information from the script context.
        let ScriptContext( Transaction(_,_,outputs,..), purpose) = ctx

        let policy_id = when purpose is {
          tx.WithdrawFrom(Inline(ScriptCredential(policy_id))) -> policy_id
          _ -> error @"Redeemer not used with staking execution"
        }

        utils.beacon_destination_check(
          dapp_hash,
          policy_id,
          outputs
        )
      }
    }
  }
}

use aiken/dict
use aiken/list
use aiken/transaction.{
  Input, Output, ScriptContext, Spend, Transaction, WithdrawFrom,
}
use aiken/transaction/credential.{Address, ScriptCredential, StakeCredential}
use amm_dex_v2/order_validation
use amm_dex_v2/types.{
  ApplyOrder, CancelExpiredOrderByAnyone, CancelOrderByOwner, OrderDatum,
  OrderRedeemer,
}
use amm_dex_v2/utils

validator(
  // the Stake Credential of Pool Batching Validator
  pool_batching_credential: StakeCredential,
  // the Stake Credential of Expired Order Cancellation Validator
  expired_order_cancel_credential: StakeCredential,
) {
  fn validate_order(raw_datum: Data, raw_redeemer: Data, context: ScriptContext) {
    let ScriptContext { transaction, purpose } = context
    expect Spend(_) = purpose
    expect redeemer: OrderRedeemer = raw_redeemer
    when redeemer is {
      ApplyOrder -> {
        let Transaction { withdrawals, .. } = transaction
        // validate that an Order can be spent if there's a `Order Batching` validator in the `withdrawals`
        dict.has_key(withdrawals, pool_batching_credential)
      }
      CancelOrderByOwner -> {
        let Transaction { inputs, extra_signatories, withdrawals, mint, .. } =
          transaction
        expect order_datum: OrderDatum = raw_datum
        let OrderDatum { canceller, .. } = order_datum
        // Canceller can be one of 4 authorization methods
        utils.authorize_order_license(
          author: canceller,
          transaction_inputs: inputs,
          withdrawals: withdrawals,
          extra_signatories: extra_signatories,
          transaction_mint: mint,
        )
      }
      CancelExpiredOrderByAnyone -> {
        let Transaction { withdrawals, .. } = transaction
        // validate that an Order can be spent if there's a `Order Batching` validator in the `withdrawals`
        dict.has_key(withdrawals, expired_order_cancel_credential)
      }
    }
  }
}

validator {
  fn validate_expired_order_cancel(
    _redeemer: Data,
    context: ScriptContext,
  ) -> Bool {
    expect ScriptContext { transaction, purpose: WithdrawFrom(_) } = context
    let Transaction { inputs, outputs, validity_range, datums, .. } =
      transaction
    // Assume all script inputs are order scripts.
    // All scripts that have the same Order Datum structure will be accepted in this transaction.
    let order_inputs =
      list.filter(
        inputs,
        fn(input) {
          let Input {
            output: Output { address: Address { payment_credential, .. }, .. },
            ..
          } = input
          when payment_credential is {
            ScriptCredential(_) -> True
            _ -> False
          }
        },
      )

    let start_valid_time_range =
      utils.must_get_finite_start_validity(validity_range)
