  // The PolicyID of Authen Minting Policy
  authen_policy_id: PolicyId,
  // Address of Pool Contract
  // This address has Pool Validator Hash as Payment Credential and Stake Credential is controlled by Minswap Labs
  pool_address: Address,
  // Stake Credential of Pool Batching Contract
  pool_batching_stake_credential: StakeCredential,
) {
  fn validate_factory(
    datum: FactoryDatum,
    redeemer: FactoryRedeemer,
    context: ScriptContext,
  ) {
    let ScriptContext { transaction, purpose } = context
    expect Spend(factory_ref) = purpose
    let Transaction { inputs, mint, outputs, .. } = transaction
    let FactoryRedeemer { asset_a, asset_b } = redeemer
    let FactoryDatum { head: current_head, tail: current_tail } = datum
    let Asset { policy_id: asset_a_policy_id, asset_name: asset_a_asset_name } =
      asset_a
    let Asset { policy_id: asset_b_policy_id, asset_name: asset_b_asset_name } =
      asset_b
    // validate that Asset A and Asset B must be sorted
    expect utils.sorted_asset(asset_a, asset_b)
    let lp_asset_name =
      utils.compute_lp_asset_name(
        asset_a_policy_id,
        asset_a_asset_name,
        asset_b_policy_id,
        asset_b_asset_name,
      )

    // validate that there's single Factory UTxO in Transaction Input and contain single legitimate Factory NFT Token
    expect Some(factory_input) =
      list.find(
        inputs,
        fn(input) {
          let Input { output_reference: out_ref, .. } = input
          out_ref == factory_ref
        },
      )
    let Input {
      output: Output {
        value: factory_input_value,
        address: factory_address,
        ..
      },
      ..
    } = factory_input
    let Address { payment_credential: factory_payment_credential, .. } =
      factory_address
    expect [_] =
      list.filter(
        inputs,
        fn(input) {
          let Input {
            output: Output {
              address: Address { payment_credential: payment_cred, .. },
              ..
            },
            ..
          } = input
          factory_payment_credential == payment_cred
        },
      )

    // Transaction must have a Factory Asset in the Spending Script
    expect
      value.without_lovelace(factory_input_value) == value.from_asset(
        authen_policy_id,
        utils.factory_auth_asset_name,
        1,
      )
    // validate that there are only 2 Factory UTxOs in Transaction Outputs, 
    // they must contain single legitimate Factory NFT Token. 
    expect [factory_output_1, factory_output_2] =
      list.filter(
        outputs,
        fn(output) {
          let Output {
            address: Address { payment_credential: payment_cred, .. },
            value: out_value,
            ..
          } = output
          and {
            factory_payment_credential == payment_cred,
            value.without_lovelace(out_value) == value.from_asset(
              authen_policy_id,
              utils.factory_auth_asset_name,
              1,
            ),
          }
        },
      )
    let Output { datum: factory_output_1_raw_datum, .. } = factory_output_1
    let Output { datum: factory_output_2_raw_datum, .. } = factory_output_2
    expect factory_outout_datum_1: FactoryDatum =
      utils.must_find_script_inline_datum(factory_output_1_raw_datum)
    expect factory_outout_datum_2: FactoryDatum =
      utils.must_find_script_inline_datum(factory_output_2_raw_datum)
    let FactoryDatum { head: new_head_1, tail: new_tail_1 } =
      factory_outout_datum_1
    let FactoryDatum { head: new_head_2, tail: new_tail_2 } =
      factory_outout_datum_2
    // validate that new Factory UTxO datum must be followed by Linked List rule
    // (old head, old tail) -> (old head, Pool LP Token Name) and (Pool LP Token Name, old tail)
    // old head < Pool LP Token Name < old tail
    expect and {
        builtin.less_than_bytearray(new_head_1, new_tail_1),
        builtin.less_than_bytearray(new_head_2, new_tail_2),
        new_head_1 == current_head,
        new_tail_2 == current_tail,
        lp_asset_name == new_tail_1,
        lp_asset_name == new_head_2,
      }
    // validate that there is a new Pool UTxO in Transaction Outputs. 
    // Pool UTxO must contain single Pool NFT Token
    expect [pool_output] =
      list.filter(
        outputs,
        fn(output) {
          let Output { address: out_addr, value: out_value, .. } = output
          let Address { payment_credential: out_addr_payment_credential, .. } =
            out_addr
          when out_addr_payment_credential is {
            ScriptCredential(_) -> and {
                pool_address == out_addr,
                value.quantity_of(
                  out_value,
                  authen_policy_id,
                  utils.pool_auth_asset_name,
                ) == 1,
              }
            _ -> False
          }
        },
      )
    let Output { value: pool_output_value, datum: pool_output_raw_datum, .. } =
      pool_output
    expect pool_out_datum: PoolDatum =
      utils.must_find_script_inline_datum(pool_output_raw_datum)
    let PoolDatum {
      pool_batching_stake_credential: pool_stake_credential,
      asset_a: pool_datum_asset_a,
      asset_b: pool_datum_asset_b,
      total_liquidity: pool_datum_total_liquidity,
      reserve_a: pool_datum_reserve_a,
      reserve_b: pool_datum_reserve_b,
      base_fee_a_numerator: pool_datum_base_fee_a_numerator,
      base_fee_b_numerator: pool_datum_base_fee_b_numerator,
      fee_sharing_numerator_opt: pool_datum_fee_sharing_numerator_opt,
      allow_dynamic_fee: pool_allow_dynamic_fee,
    } = pool_out_datum
    let estimated_amount_a =
      value.quantity_of(
        pool_output_value,
        asset_a_policy_id,
        asset_a_asset_name,
      )
    let amount_a =
      if utils.is_ada_asset(asset_a_policy_id, asset_a_asset_name) {
        estimated_amount_a - utils.min_pool_ada
      } else {
        estimated_amount_a
      }
    let amount_b =
      value.quantity_of(
        pool_output_value,
        asset_b_policy_id,
        asset_b_asset_name,
      )
    let total_liquidity = math.calculate_initial_liquidity(amount_a, amount_b)
    let remaining_liquidity =
      9223372036854775807 - ( total_liquidity - utils.default_burn_liquidity )
    let expected_pool_out_value =
      value.zero()
        |> value.add(ada_policy_id, ada_asset_name, utils.min_pool_ada)
        |> value.add(asset_a_policy_id, asset_a_asset_name, amount_a)
        |> value.add(asset_b_policy_id, asset_b_asset_name, amount_b)
        |> value.add(authen_policy_id, lp_asset_name, remaining_liquidity)
        |> value.add(authen_policy_id, utils.pool_auth_asset_name, 1)
    and {
      // stake_credential must be the same with configured credential
      pool_stake_credential == pool_batching_stake_credential,
      // asset_a and asset_b must be the same with Factory Redeemer
      pool_datum_asset_a == asset_a,
      pool_datum_asset_b == asset_b,
      // Total Liquidity in PoolDatum must be sqrt(amount_a * amount_b)
      pool_datum_total_liquidity == total_liquidity,
      // Pool Reserve must be the same between datum and value 
      pool_datum_reserve_a == amount_a,
      pool_datum_reserve_b == amount_b,
      // Initial Pool Reserves must be positive and the liquditiy must be greater than the burn liquidity
      amount_a > 0,
      amount_b > 0,
      total_liquidity > utils.default_burn_liquidity,
      // base_fee_a_numerator and base_fee_b_numerator must be the same
      pool_datum_base_fee_a_numerator == pool_datum_base_fee_b_numerator,
      // base_fee_a_numerator and base_fee_b_numerator must be between **5** and **2000**
      pool_validation.validate_fee_percent(
        fee_num: pool_datum_base_fee_a_numerator,
        max_fee_num: utils.max_base_fee_numerator,
        min_fee_num: utils.min_base_fee_numerator,
      ),
      // Dynamic Fee is not allowed in Pool Creation 
      pool_allow_dynamic_fee == False,
      // fee_sharing_numerator_opt must be empty
      pool_datum_fee_sharing_numerator_opt == None,
      // Transaction must mint necessary assets
      value.from_minted_value(mint) == pool_validation.get_pool_creation_expected_mint(
        authen_policy_id: authen_policy_id,
        lp_asset_name: lp_asset_name,
      ),
      // Pool Value must only have necessary Token:
      // - Asset A 
      // - Asset B
      // - remaining LP Token (_MAX_INT64_ - _total_liquidity_)
      // - 1 Pool NFT Token
      // - 3 ADA (required ADA for an UTxO)
      expected_pool_out_value == pool_output_value,
    }
  }
}

use aiken/collection/list
use cardano/address.{Script}
use cardano/assets.{PolicyId}
use cardano/transaction.{InlineDatum, OutputReference, Transaction}
use shared.{spent_output}
use sundae/multisig
use types/settings.{
  SettingsAdminUpdate, SettingsDatum, SettingsRedeemer, TreasuryAdminUpdate,
  settings_nft_name,
} as types_settings

/// The settings validator lets the settings and treasury admins update global settings for the protocol
///
/// It is parameterized by the protocol_boot_utxo, a constant to make it an NFT by the usual trick.
validator settings(protocol_boot_utxo: OutputReference) {
  spend(
    input_datum: Option<SettingsDatum>,
    redeemer: SettingsRedeemer,
    out_ref: OutputReference,
    transaction: Transaction,
  ) {
    // Find our own input so we know the datum / our own address
    let own_input = spent_output(transaction, out_ref)
    let own_address = own_input.address

    // Then find our continuing output;
    // We can assume the first output is the settings output
    // This is safe because the ledger doesn't reorder the outputs,
    // and if someone constructs it out of order, it'll just fail because the address / value will change
    // since the NFT is unique
    expect Some(own_output) = list.head(transaction.outputs)
    expect own_output.address == own_address
    // It's also important to enforce that this is an inline datum;
    // setting a datum hash could cause a bunch of off-chain infrastructure to break
    expect InlineDatum(output_datum) = own_output.datum
    expect output_datum: SettingsDatum = output_datum

    expect Some(input_datum) = input_datum

    // We check that the value on the input and output are equal, to ensure the settings NFT gets paid back into the output
    // We compare `without_lovelace` to leave full freedom for ADA to be added/removed as the minUTXO requirement changes
    // Note that this can only be spent by the SettingsAdmin or TreasuryAdmin, so we won't leak a small amount of ADA to arbitrary users
    // Also, it is not expected that the ADA ever be more than the minUTXO cost, so this doesn't expose hundreds of ADA (for example) to the treasury admin
    let value_not_changed =
      assets.without_lovelace(own_output.value) == assets.without_lovelace(
        own_input.value,
      )

    // Make sure we don't mint anything, otherwise someone might mint another settings token
    let no_mint = transaction.mint == assets.zero

    // Then, depending on which admin is doing the updating, different settings are updatable
    when redeemer is {
      // In the case of the settings admin
      SettingsAdminUpdate -> {
        // First, make sure the settings admin credentials (for example, a multisig) are satisfied
        // TODO: We could also require a signature from the new admin too, as a guard against accidentally bricking the protocol
        // TODO: We could also require a signature from the new treasury admin, if we're updating the treasury admin;
        // for example, this would ensure we don't set the treasury admin
        // so large that it exceeds execution units?
        let signed_by_admin =
          multisig.satisfied(
            input_datum.settings_admin,
            transaction.extra_signatories,
            transaction.validity_range,
            transaction.withdrawals,
          )

        // Settings admin can change any datum fields except for these
        let allowed_datum =
          SettingsDatum {
            ..// Most fields *can* be updated by the admin, so we start from the output datum
            output_datum,
            authorized_staking_keys: // But ensure that these fields haven't been changed
            input_datum.authorized_staking_keys,
            treasury_address: input_datum.treasury_address,
            treasury_allowance: input_datum.treasury_allowance,
          }

        // TODO: move base_fee, simple_fee, and strategy_fee to the treasury admin instead, maybe?
        // TODO: enforce maximum scooper keys to some large N, to prevent locking the datum?
        and {
          signed_by_admin,
          output_datum == allowed_datum,
          value_not_changed,
          no_mint,
        }
      }
      TreasuryAdminUpdate -> {
        let signed_by_admin =
          multisig.satisfied(
            input_datum.treasury_admin,
            transaction.extra_signatories,
            transaction.validity_range,
            transaction.withdrawals,
          )

        // Treasury admin can change any datum fields except for these
        // i.e. can change the treasury address, treasury allowance, and the authorized staking keys
        let allowed_datum =
          SettingsDatum {
            ..// Most of the fields can't change, so we start from the input datum
            input_datum,
            authorized_staking_keys: // These three can be updated, so we pull whatever is in the output datum
            output_datum.authorized_staking_keys,
            treasury_address: output_datum.treasury_address,
            treasury_allowance: output_datum.treasury_allowance,
          }

        // TODO: enforce maximum staking keys to some large N, to prevent locking the Datum
        and {
          signed_by_admin,
          output_datum == allowed_datum,
          value_not_changed,
          no_mint,
        }
      }
    }
  }

  // Let us mint the settings NFT exactly once, by checking that one of the inputs is the protocol_boot_utxo
  mint(_r: Data, own_policy_id: PolicyId, transaction: Transaction) {
    let expected_mint = assets.from_asset(own_policy_id, settings_nft_name, 1)
    // Check that we mint *only* one token, and it's exactly our own policy id, and the settings NFT name
    // This ensures that we don't sneakily mint a second NFT for "reasons"
    let mints_exactly_one_settings_nft = transaction.mint == expected_mint

    // And, like mentioned above, ensure that this is a true NFT
    let spends_protocol_boot_utxo =
      list.any(
        transaction.inputs,
        fn(input) { input.output_reference == protocol_boot_utxo },
      )

    // Make sure the output value contains no extra tokens, and is paid to the settings script itself
    expect [settings_output] =
      list.filter(
        transaction.outputs,
        fn(output) { assets.without_lovelace(output.value) == expected_mint },
      )
    let pays_to_settings_script =
      settings_output.address.payment_credential == Script(own_policy_id)
    // Make sure the datum is an inline datum, and has a well-formed datum
    expect InlineDatum(settings_datum) = settings_output.datum
    expect _: SettingsDatum = settings_datum

    // TODO: require a signature from the 3 initial admins (or at least the settings admin) to prevent bricking?
    and {
      mints_exactly_one_settings_nft,
      spends_protocol_boot_utxo,
      pays_to_settings_script,
    }
  }

  else(_) {
    fail
  }
}

use aiken/collection/list
use aiken/collection/pairs
use cardano/assets.{PolicyId}
use cardano/script_context.{Publishing, ScriptContext, Withdrawing}
use cardano/transaction.{InlineDatum, Transaction}
use sundae/multisig
use types/settings.{find_settings_datum}

/// The pool stake validator is one example script staking validator that could be attached to each pool, to delegate to a stake pool operator
///
/// It is parameterized by the settings policy ID, so we can check for the treasury administrator,
/// and an arbitrary _instance property. This can be treated as a nonce, so we can generate N different script hashes with the same behavior
/// so we can delegate to different pools.
///
/// Note that this scheme is highly future proof; when the Conway hardfork happens and adds new script purposes
/// for governance, normally that would mean the protocol could never take advantage of them, because the Plutus v2 scripts
/// would fail on the new script context.
/// However, in this case, the treasury admin can simply update to a new staking policy, rotate that onto the pools, and then use
/// the new features. This is because the staking script doesn't actually run when the pool runs! it only runs in its own transaction
/// so the fact that it becomes Plutus v66 makes no difference.
validator pool_stake(settings_policy_id: PolicyId, _instance: Int) {
  else(ctx: ScriptContext) {
    // Deconstruct the script context and transaction to pull off the fields we'll need
    // This is to avoid destructing many times, because each field access is O(n)
    let ScriptContext { transaction, info: purpose, .. } = ctx
    let Transaction {
      reference_inputs,
      extra_signatories,
      validity_range,
      withdrawals,
      outputs,
      ..
    } = transaction

    // And find the settings datum; this ensures it exists, and lets us look at the settings datum
    let settings_datum =
      find_settings_datum(reference_inputs, settings_policy_id)
    // Regardless of what we're doing, the treasury administrator conditions must be satisfied (ex: multisig, DAO script, etc)
    expect
      multisig.satisfied(
        settings_datum.treasury_admin,
        extra_signatories,
        validity_range,
        withdrawals,
      )

    when purpose is {
      // To withdraw any amount of earned staking rewards,
      Withdrawing(_) -> {
        // We calculate the amount withdrawn by summing up *all* withdrawals
        // This is to avoid a double satisfaction problem, where withdrawing 100 ADA of rewards from
        // two different scripts each believe that 100 ADA has been paid to the treasury
        let amount_withdrawn =
          pairs.foldl(withdrawals, 0, fn(_k, v, s) { v + s })
        // Allow the treasury administrator to keep some portion of the withdrawn rewards, for administrative costs like paying the scoopers
        // We multiply first, then divide, for maximal precision
        let allowance =
          amount_withdrawn * settings_datum.treasury_allowance.1st / settings_datum.treasury_allowance.2nd
        // The amount that must be sent to the treasury is the total amount withdrawn, minus the allowance that the adminsitrator can keep
        let to_treasury = amount_withdrawn - allowance
        // Find the first output to the treasury address; We assume there's only one output that receives the whole amount,
        // as there's not much benefit to splitting it up. TODO: is this true? could the treasury administrator want to earmark them separately or something?
        // If we find no treasury output, the transaction fails here.
        // We compare the *whole* address here, instead of the payment credential, because the treasury should also be earning staking rewards!
        expect Some(treasury_output) =
          list.find(
            outputs,
            fn(o) { o.address == settings_datum.treasury_address },
          )
        // Make sure *at least* the amount we calculated is sent to the treasury; it can be larger,
        // if someone is feeling generous, or to deal with rounding for example, but it cannot be less
        expect assets.lovelace_of(treasury_output.value) >= to_treasury
        // We enforce that the treasury is paid with a null datum
        // Sending to a script with *no* datum is very dangerous, and letting an arbitrary datum could
        // allow shenaigans.
        // If we wish to have a treasury script with a specific datum structure, then we can implement a proxy script and set that as the
        // treasury address
        treasury_output.datum == InlineDatum(Void)
      }
      // Publishing a cert succeeds, because we've already checked that the multisig script was satisfied above
      Publishing(..) -> True
      // Prevent minting or spending, just in case
      _ -> False
    }
  }
}

