          tx.Spend(input_ref)
        ) = ctx
        expect Some(Input(_, Output(addr,..))) = tx.find_input(inputs,input_ref)
        let SwapDatum(beacon_id,..) = datum

        // The address' staking credential must signal approval (Requirement 1).
        trace_if_false( 
          @"Staking credential did not approve", 
          common_utils.staking_credential_approves( addr, wtdr, sigs)
        ) &&

        // The beacon_script was not executed as a staking script.
        trace_if_false(
          @"Beacon script not executed as staking script",
          common_utils.has_beacon_script_staking_execution(beacon_id,wtdr)
        )
      }

      // Take asset1 and deposit asset2. It compares the input value against the corresponding output
      // value to determine the value flux.
      TakeAsset1 -> {
        // Requirements:
        // 1) The input must contain the beacons for that trading pair.
        // 2) There must must be an output to this address with the proper value and inline
        //    SwapDatum:
        //      a) Must contain exactly 1 of each of the required beacons.
        //      b) The swap datum must be exactly the same as the input's except the prev_input
        //         must be == Some(input_ref).
        // 3) Asset1 taken * price <= Asset2 given
        // 4) Only Asset1 leaves and only Asset2 is deposited. Ada can always be deposited.
        //
        // Requirement 1 guarantees that all invalid UTxOs belong to the address owner and that
        // swap inputs have a valid price:
        //      a) `asset1_price` denominator > 0
        //      b) `asset1_price` numerator > 0
        //
        // Requirement 2 guarantees that beacons from other trading pairs cannot be combined into
        // one output UTxO; all swaps must get their own swap UTxOs.
        //
        // Requirement 4 helps optimize beacon queries by keeping UTxO asset lists to the smallest
        // possible size.

        // Get required information for script context.
        expect ScriptContext( 
          Transaction(inputs,_,outputs,..), 
          tx.Spend(input_ref)
        ) = ctx
        expect Some(Input(_, Output(addr,val,..))) = tx.find_input(inputs,input_ref)

        // Extract out the information from this datum.
        let SwapDatum(
          beacon_id, 
          pair_beacon,
          asset1_id,
          asset1_name,
          asset1_beacon,
          asset2_id,
          asset2_name,
          asset2_beacon,
          asset1_price,
          asset2_price,
          _ // prev_input is not needed.
        ) = datum

        // The following checks requirements 1,2,3,4. It has its own error messages.
        utils.valid_swap(
          input_ref,
          val, // Input's value.
          addr, // This address.
          beacon_id,
          pair_beacon,
          asset1_id,
          asset1_name,
          asset1_beacon,
          asset2_id,
          asset2_name,
          asset2_beacon,
          asset1_price,
          asset2_price,
          outputs,
          False // Not taking asset2.
        )
      }

      // Take asset2 and deposit asset1. It compares the input value against the corresponding output
      // value to determine the value flux. 
      TakeAsset2 -> {
        // Requirements:
        // 1) The input must contain the beacons for that trading pair.
        // 2) There must must be an output to this address with the proper value and inline
        //    SwapDatum:
        //      a) Must contain exactly 1 of each of the required beacons.
        //      b) The swap datum must be exactly the same as the input's except the prev_input
        //         must be == Some(input_ref).
        // 3) Asset1 taken * price <= Asset2 given
        // 4) Only Asset1 leaves and only Asset2 is deposited. Ada can always be deposited.
        //
        // Requirement 1 guarantees that all invalid UTxOs belong to the address owner and that
        // swap inputs have a valid price:
        //      a) `asset2_price` denominator > 0
        //      b) `asset2_price` numerator > 0
        //
        // Requirement 2 guarantees that beacons from other trading pairs cannot be combined into
        // one output UTxO; all swaps must get their own swap UTxOs.
        //
        // Requirement 4 helps optimize beacon queries by keeping UTxO asset lists to the smallest
        // possible size.

        // Get required information for script context.
        expect ScriptContext( 
          Transaction(inputs,_,outputs,..), 
          tx.Spend(input_ref)
        ) = ctx
        expect Some(Input(_, Output(addr,val,..))) = tx.find_input(inputs,input_ref)

        // Extract out the information from this datum.
        let SwapDatum(
          beacon_id, 
          pair_beacon,
          asset1_id,
          asset1_name,
          asset1_beacon,
          asset2_id,
          asset2_name,
          asset2_beacon,
          asset1_price,
          asset2_price,
          _ // prev_input is not needed.
        ) = datum

        // The following checks requirements 1,2,3,4. It has its own error messages.
        utils.valid_swap(
          input_ref,
          val, // Input's value.
          addr, // This address.
          beacon_id,
          pair_beacon,
          asset1_id,
          asset1_name,
          asset1_beacon,
          asset2_id,
          asset2_name,
          asset2_beacon,
          asset1_price,
          asset2_price,
          outputs,
          True // Taking asset2.
        )
      }
    }
  }
}

validator(dapp_hash: Hash<Blake2b_224, Script>) {
  fn beacon_script(redeemer: BeaconRedeemer, ctx: ScriptContext) -> Bool {
    when redeemer is {
      CreateOrCloseSwaps -> {
        // Requirements:
        // 1) Must be executed as a minting script.
        // 2) The beacons must go to an address protected by the DApp validator script.
        // 3) The beacons must go to an address using a valid staking credential.
        // 4) The UTxOs with the beacons must have the proper value:
        //      a) Exactly three kinds of beacons: pair beacon, asset1 beacon, and asset2 beacon.
        //      b) The beacons must correspond to the beacons in the datum.
        //      c) There must be exactly 1 of each beacon.
        //      d) No extraneous assets are in the UTxO.
        // 5) The beacons must be stored with the proper inline `SwapDatum`:
        //      a) `beacon_id` == this policy id.
        //      b) `pair_beacon` == sha2_256(asset1_id ++ asset1_name ++ asset2_id ++ asset2_name)
        //      c) `asset1_id` == policy id of asset1 for that trading pair.
        //      d) `asset1_name` == asset name of asset1 for that trading_pair.
        //      e) `asset1_beacon` == sha2_256(asset1_id ++ asset1_name).
        //      f) `asset2_id` == policy id of asset2 for that trading_pair.
        //      g) `asset2_name` == asset name of asset2 for that trading_pair.
        //      h) `asset2_beacon` == sha2_256(asset2_id ++ asset2_name).
        //      i) `asset1_price` denominator > 0
        //      j) `asset1_price` numerator > 0
        //      k) `asset2_price` denominator > 0
        //      l) `asset2_price` numerator > 0
        //      m) asset1 < asset2
        //
        // The validator assumes the trading pairs are sorted which is why asset1 must be less than
        // asset2. Asset1 and asset2 cannot be the same asset.

        // Get required information from the script context.
        let ScriptContext( Transaction(_,_,outputs,..), purpose) = ctx

        let policy_id = when purpose is {
          tx.Mint(policy_id) -> policy_id
          _ -> error @"Redeemer not used with minting execution"
        }

        utils.beacon_destination_check(
          dapp_hash,
          policy_id,
          outputs
        )
      }

      UpdateSwaps -> {
        // Requirements:
        // 1) Must be executed as a staking script.
        // 2) The beacons must go to an address protected by the DApp validator script.
        // 3) The beacons must go to an address using a valid staking credential.
        // 4) The UTxOs with the beacons must have the proper value:
        //      a) Exactly three kinds of beacons: pair beacon, asset1 beacon, and asset2 beacon.
        //      b) The beacons must correspond to the beacons in the datum.
        //      c) There must be exactly 1 of each beacon.
        //      d) No extraneous assets are in the UTxO.
        // 5) The beacons must be stored with the proper inline `SwapDatum`:
        //      a) `beacon_id` == this policy id.
        //      b) `pair_beacon` == sha2_256(asset1_id ++ asset1_name ++ asset2_id ++ asset2_name)
        //      c) `asset1_id` == policy id of asset1 for that trading pair.
        //      d) `asset1_name` == asset name of asset1 for that trading_pair.
        //      e) `asset1_beacon` == sha2_256(asset1_id ++ asset1_name).
        //      f) `asset2_id` == policy id of asset2 for that trading_pair.
        //      g) `asset2_name` == asset name of asset2 for that trading_pair.
        //      h) `asset2_beacon` == sha2_256(asset2_id ++ asset2_name).
        //      i) `asset1_price` denominator > 0
        //      j) `asset1_price` numerator > 0
        //      k) `asset2_price` denominator > 0
        //      l) `asset2_price` numerator > 0
        //      m) asset1 < asset2
        //
        // The validator assumes the trading pairs are sorted which is why asset1 must be less than
        // asset2. Asset1 and asset2 cannot be the same asset.

        // Get required information from the script context.
        let ScriptContext( Transaction(_,_,outputs,..), purpose) = ctx

        let policy_id = when purpose is {
          tx.WithdrawFrom(Inline(ScriptCredential(policy_id))) -> policy_id
          _ -> error @"Redeemer not used with staking execution"
        }

        utils.beacon_destination_check(
          dapp_hash,
          policy_id,
          outputs
        )
      }
    }
  }
}

use aiken/transaction.{Mint, ScriptContext, Transaction}
use aiken/transaction/credential.{Address}
use aiken/transaction/value.{AssetName, PolicyId}
use assist/addresses
use assist/find
use assist/minting
use assist/payout
use assist/signing
use assist/types/cip68.{CIP68}
use assist/types/hashes.{PublicKeyHash, TxHash, ValidatorHash}
use assist/values
use mint_contract/types.{BurnTokens, MintRedeemer, MintTokens}

// This allows a hot key to mint 1 reference and 1 nft for a cip 68 token or
// the hot key may be either the reference or nft.
validator(
  // this key controls everything
  hot_key: PublicKeyHash,
  // the cip 68 and stake data
  cip68_hash_data: Data,
  stake_hash_data: Data,
  // this allows many policy ids
  _random_string: ByteArray,
) {
  fn params(redeemer: MintRedeemer, context: ScriptContext) -> Bool {
    // this transaction
    let tx: Transaction = context.transaction
    // the minted value from the tx
    let mint: List<(PolicyId, AssetName, Int)> =
      tx.mint
        |> value.from_minted_value()
        |> value.flatten()
    // mint only
    when context.purpose is {
      // mint 1 ref and 1 nft
      Mint(own_currency_symbol) ->
        when redeemer is {
          MintTokens -> {
            // cip68 address know at compile time
            expect cip68_hash: ValidatorHash = cip68_hash_data
            expect stake_hash: ValidatorHash = stake_hash_data
            // staking at this address
            let cip68_addr: Address =
              addresses.create_script_address(cip68_hash, stake_hash)
            // get first input info from the tx in lexographical order
            let first_index: Int = find.first_input_index(tx.inputs)
            let first_tx_hash: TxHash = find.first_input_txid(tx.inputs)
            // use the reference prefix
            let ref_tkn: AssetName =
              values.unique_token_name(
                first_tx_hash,
                first_index,
                cip68.prefix_100,
              )
            // use the nft prefix
            let nft_tkn: AssetName =
              values.unique_token_name(
                first_tx_hash,
                first_index,
                cip68.prefix_222,
              )
            // this should prevent utxos without cip68 datum to go to the cip68 storage contract
            // This Forces An Inline Datum!
            expect _: CIP68 = find.output_datum_by_addr(tx.outputs, cip68_addr)
            //
            // The hot key has to sign the tx to mint 1 ref and 1 nft. There
            // is destination validation for the cip68 token but not for the 
            // nft. This should allow a lot of flexability.
            //
            and {
              // cornucopias must sign it
              signing.verify_sig(tx.extra_signatories, hot_key)?,
              // prevent roll over double nft attack
              (first_index < 256)?,
              // must mint 1 reference token
              minting.exact(mint, own_currency_symbol, ref_tkn, 1)?,
              // the nft can go anywhere; ref must go to metadatum contract
              // must mint 1 non-fungible token
              minting.exact(mint, own_currency_symbol, nft_tkn, 1)?,
              // reference token must go to cip contract
              payout.at_least(
                cip68_addr,
                value.from_asset(own_currency_symbol, ref_tkn, 1),
                tx.outputs,
              )?,
            }
          }
          // burn the ref or the nft or both at the same time
          BurnTokens -> and {
              // cornucopias must sign it
              signing.verify_sig(tx.extra_signatories, hot_key)?,
              // this allows optional burning of either or both
              or {
                // burn only 1 ref token
                minting.by_prefix(
                  mint,
                  own_currency_symbol,
                  cip68.prefix_100,
                  -1,
                )?,
                // burn only 1 nft
                minting.by_prefix(
                  mint,
                  own_currency_symbol,
                  cip68.prefix_222,
                  -1,
                )?,
              }?,
            }
        }
      // Mint Only
      _ -> False
    }
  }
}

use aada/types/pool
use aiken/dict
use aiken/list
use aiken/transaction.{InlineDatum, Mint, OutputReference, ScriptContext}
use aiken/transaction/value.{flatten, from_minted_value, quantity_of, tokens}

type TokenRedeemer {
  TransitionPool {
    pool_oref: OutputReference,
    continuing_output: pool.OutputIndex,
  }
  CreatePool { produced_output: pool.OutputIndex }
  DestroyPool { pool_oref: OutputReference }
}

// Liquidity token is a representation of the pool liquidity.

validator(pool_hash: ByteArray, token_name: ByteArray) {
  fn liquidity_token(redeemer: TokenRedeemer, ctx: ScriptContext) {
    expect Mint(own_policy) = ctx.purpose
    when redeemer is {
      // Deposit or Withdraw redeemer
      TransitionPool { pool_oref, continuing_output } -> {
        expect Some(pool_input) =
          ctx.transaction.inputs
            |> list.find(fn(inp) { inp.output_reference == pool_oref })
        expect Some(pool_output) =
          ctx.transaction.outputs |> list.at(continuing_output)
        expect InlineDatum(input_datum) = pool_input.output.datum
        expect input_datum: pool.Datum = input_datum
        expect InlineDatum(output_datum) = pool_output.datum
        expect output_datum: pool.Datum = output_datum
        let pool_addresses_matches =
          pool_input.output.address == pool_output.address
        let id = input_datum.params.pool_nft_name

        let dust_attack_check =
          when flatten(pool_input.output.value) is {
            [(_, _, _)] -> False
            [(_, _, _), (_, _, _)] -> True
            [(_, _, _), (_, _, _), (_, _, _)] -> True
            _ -> False
          }
        // Makes sure Pool NFT is present as well as it's in the output
        let nfts_check =
          quantity_of(pool_input.output.value, pool_hash, id) == 1 && quantity_of(
            pool_output.value,
            pool_hash,
            id,
          ) == 1

        expect [(lp_token_name, lp_tokens_amount)] =
          tokens(from_minted_value(ctx.transaction.mint), own_policy)
            |> dict.to_list()

        // LP token name must match LP token name which is also pool Stake-creds hash
        let valid_lp_token_name =
          lp_token_name == output_datum.params.pool_nft_name && lp_token_name == token_name
        let valid_lp_token_change =
          output_datum.total_lp_tokens - input_datum.total_lp_tokens == lp_tokens_amount
        and {
          valid_lp_token_change,
          valid_lp_token_name,
          nfts_check,
          dust_attack_check,
          pool_addresses_matches,
        }
      }
      DestroyPool { pool_oref } -> {
        expect Some(pool_input) =
          ctx.transaction.inputs
            |> list.find(fn(inp) { inp.output_reference == pool_oref })
        expect InlineDatum(input_datum) = pool_input.output.datum
        expect input_datum: pool.Datum = input_datum

        let nfts_check =
          quantity_of(pool_input.output.value, pool_hash, token_name) == 1
        let nft_name_datum_check =
          input_datum.params.pool_nft_name == token_name

        let actual_tokens_minted =
          quantity_of(
            from_minted_value(ctx.transaction.mint),
            own_policy,
            token_name,
          )

        let tokens_mint_check =
          actual_tokens_minted + input_datum.total_lp_tokens == 0

        and {
          tokens_mint_check?,
          nfts_check?,
          nft_name_datum_check?,
        }
      }
      CreatePool { produced_output } -> {
        expect Some(pool_output) =
          ctx.transaction.outputs |> list.at(produced_output)
        expect InlineDatum(output_datum) = pool_output.datum
        expect output_datum: pool.Datum = output_datum
        let mint = from_minted_value(ctx.transaction.mint)

        // Expects only one tokenName to be minted
        expect [(pool_token_name, 1)] = dict.to_list(tokens(mint, pool_hash))

        // Doing an extra check for all the name matching
        let correct_pooltoken_name = and {
            pool_token_name == output_datum.params.pool_nft_name,
            token_name == pool_token_name,
          }

        let lp_tokens_minted = quantity_of(mint, own_policy, pool_token_name)
        let depositing_tokens_count_actual =
          quantity_of(
            pool_output.value,
            output_datum.params.loan_cs.policy_id,
            output_datum.params.loan_cs.asset_name,
          )
        let correct_pooltoken_out =
          quantity_of(pool_output.value, pool_hash, pool_token_name) == 1

        let correct_recorded_lptokens =
          output_datum.total_lp_tokens == lp_tokens_minted

        let depositing_gt_0 = depositing_tokens_count_actual > 0
        let depositing_at_script =
          depositing_tokens_count_actual == output_datum.balance

        let minted_lp_match_deposit =
          depositing_tokens_count_actual == lp_tokens_minted
        and {
          correct_pooltoken_out?,
          correct_recorded_lptokens?,
          depositing_gt_0?,
          depositing_at_script?,
          minted_lp_match_deposit?,
          correct_pooltoken_name?,
        }
      }
    }
  }
}

