        trace_if_false( 
          @"Staking credential did not approve", 
          common_utils.staking_credential_approves( addr, wtdr, sigs)
        ) &&

        // The beacon_script was not executed as a minting policy.
        trace_if_false(
          @"Beacon script not executed as minting policy",
