  fn placeholder_nft(r: NFTRedeemerElement<Void>, ctx: ScriptContext) {
    let ScriptContext { transaction: Transaction { inputs, mint, .. }, purpose } =
      ctx
    let NFTRedeemerElement { action, .. } = r
