  if flag_datum == 0 {
    NoDatum
  } else {
    InlineDatum(datum)
  }
}

pub fn all_inputs_present(
  utxos_chosen: List<OutputReference>,
  real_utxos: List<Input>,
) {
  list.all(utxos_chosen, fn(input) { is_some(find_input(real_utxos, input)) })
}

pub fn only_external_signer_inputs_extra(
  utxos_chosen: List<OutputReference>,
  real_utxos: List<Input>,
  address_signer: Address,
) {
  let list_external =
    list.filter(
      real_utxos,
      fn(input) { !list.has(utxos_chosen, input.output_reference) },
    )
  list.all(list_external, fn(input) { input.output.address == address_signer })
}

pub fn find_treshold(
  treshold: Int,
  signatures: List<Multisignature>,
  signers: List<ByteArray>,
  hash: ByteArray,
  hash_eth: ByteArray,
  tx_signers: List<ByteArray>,
) {
  let unique_crosschain_signatures = list.unique(signatures)

  let valid_signatures_crosschain =
    list.foldl(
      unique_crosschain_signatures,
      0,
      fn(multisignature, valid_signatures_crosschain) -> Int {
        expect Some(verification_key) =
          list.at(signers, multisignature.position)
        if builtin.verify_ecdsa_secp256k1_signature(
          verification_key,
          hash,
          multisignature.signature,
        ) {
          valid_signatures_crosschain + 1
        } else {
          if builtin.verify_ecdsa_secp256k1_signature(
            verification_key,
            hash_eth,
            multisignature.signature,
          ) {
            valid_signatures_crosschain + 1
          } else {
            valid_signatures_crosschain
          }
        }
      },
    )

  let valid_signatures_cardano =
    list.foldl(
      tx_signers,
      0,
      fn(signer, valid_signatures_cardano) -> Int {
        if list.any(signers, fn(signer_stored) { signer_stored == signer }) {
          valid_signatures_cardano + 1
        } else {
          valid_signatures_cardano
        }
      },
    )

  valid_signatures_crosschain + valid_signatures_cardano >= treshold
}

use aiken/builtin
use aiken/list
use aiken/transaction.{
  InlineDatum, Input, Output, ScriptContext, Spend, Transaction,
}
use aiken/transaction/credential.{Address, ScriptCredential}
use aiken/transaction/value
use lb_v2/cancel_validation.{validate_cancel_lbe}
use lb_v2/order_validation.{
  validate_collect_orders, validate_redeem, validate_refund,
}
use lb_v2/treasury_validation.{
  validate_create_dex_pool, validate_creating_treasury_out,
}
use lb_v2/types.{
  CancelLBE, CloseEvent, CollectManager, CollectOrders, CreateAmmPool, PoolDatum,
  RedeemOrders, TreasuryDatum, TreasuryRedeemer, UpdateLBE,
}
use lb_v2/utils.{factory_auth_an, must_get_end_validity, treasury_auth_an}
use lb_v2/validation

validator {
  fn validate_treasury_spending(
    treasury_in_datum: TreasuryDatum,
    redeemer: TreasuryRedeemer,
    context: ScriptContext,
  ) {
    expect ScriptContext { purpose: Spend(out_ref), transaction } = context
    let Transaction {
      inputs,
      outputs,
      mint,
      validity_range,
      reference_inputs,
      extra_signatories,
      ..
    } = transaction
    expect Some(treasury_input) =
      list.find(
        inputs,
        fn(input) {
          let Input { output_reference, .. } = input
          output_reference == out_ref
        },
      )
    // Extract some necessary data.
    expect Input {
      output: Output {
        address: Address {
          payment_credential: ScriptCredential(treasury_hash),
          ..
        },
        value: treasury_in_value,
        ..
      },
      ..
    } = treasury_input
    let TreasuryDatum {
      factory_policy_id,
      manager_hash,
      order_hash,
      is_cancelled,
      ..
    } = treasury_in_datum
    let mint_value = value.from_minted_value(mint)
    let end_valid_time_range = must_get_end_validity(validity_range)
    expect and {
        value.quantity_of(
          treasury_in_value,
          factory_policy_id,
          treasury_auth_an,
        ) == 1,
        list.filter(
          inputs,
          fn(input) {
            let Input {
              output: Output { address: Address { payment_credential, .. }, .. },
              ..
            } = input
            payment_credential == ScriptCredential(treasury_hash)
          },
        ) == [treasury_input],
      }
    when redeemer is {
      // UpdateLBE before discovery phase
      UpdateLBE -> {
        let TreasuryDatum {
          base_asset,
          raise_asset,
          owner,
          start_time,
          seller_hash,
          is_cancelled,
          ..
        } = treasury_in_datum
        let Address { payment_credential: owner_payment_credential, .. } = owner
        let treasury_output =
          validation.get_treasury_output(
            outputs,
            factory_policy_id,
            treasury_hash,
          )
        let Transaction { datums, .. } = transaction
        and {
          // before discovery phase
          end_valid_time_range < start_time,
          // prevent updating the LBE when it has been cancelled.
          is_cancelled == False,
          // Authorize by owner in input treasury datum 
          validation.validate_authorize_by_owner(
            owner_payment_credential: owner_payment_credential,
            extra_signatories: extra_signatories,
            inputs: inputs,
          ),
          // create new one
          validate_creating_treasury_out(
            treasury_out: treasury_output,
            base_asset: base_asset,
            raise_asset: raise_asset,
            manager_hash: manager_hash,
            seller_hash: seller_hash,
            order_hash: order_hash,
            factory_policy_id: factory_policy_id,
            end_valid_time_range: end_valid_time_range,
            datums: datums,
            extra_signatories: extra_signatories,
            inputs: inputs,
          ),
          // mint nothing
          mint_value == value.zero(),
        }
      }
      CollectManager ->
        validation.validate_collect_manager(
          inputs: inputs,
          outputs: outputs,
          treasury_in_value: treasury_in_value,
          treasury_in_datum: treasury_in_datum,
          mint_value: mint_value,
          factory_policy_id: factory_policy_id,
          manager_hash: manager_hash,
          treasury_hash: treasury_hash,
        )
      CollectOrders ->
        validate_collect_orders(
          inputs: inputs,
          outputs: outputs,
          treasury_in_datum: treasury_in_datum,
          treasury_in_value: treasury_in_value,
          mint_value: mint_value,
          treasury_hash: treasury_hash,
        )
      CloseEvent -> {
        // forward logic to factory with case 2 factory inputs
        expect [_, _] =
          list.filter(
            inputs,
            fn(input) {
              let Input {
                output: Output {
                  address: Address { payment_credential, .. },
                  value: out_value,
                  ..
                },
                ..
              } = input
              and {
                payment_credential == ScriptCredential(factory_policy_id),
                value.quantity_of(out_value, factory_policy_id, factory_auth_an) == 1,
              }
            },
          )
        True
      }
      CreateAmmPool -> {
        let treasury_output =
          validation.get_treasury_output(
            outputs,
            factory_policy_id,
            treasury_hash,
          )
        let pool_output = validation.get_amm_pool_output(outputs)
        expect Output { datum: InlineDatum(pool_raw_datum), .. } = pool_output
        expect amm_pool_datum: PoolDatum = pool_raw_datum
        let receiver_output = builtin.head_list(outputs)
        validate_create_dex_pool(
          treasury_in_datum: treasury_in_datum,
          treasury_output: treasury_output,
          mint_value: mint_value,
          amm_pool_datum: amm_pool_datum,
          factory_policy_id: factory_policy_id,
          receiver_output: receiver_output,
        )
      }
      RedeemOrders ->
        when is_cancelled is {
          True ->
            validate_refund(
              inputs: inputs,
              outputs: outputs,
              treasury_in_datum: treasury_in_datum,
              treasury_in_value: treasury_in_value,
              mint_value: mint_value,
              treasury_hash: treasury_hash,
            )
          False ->
            validate_redeem(
              inputs: inputs,
              outputs: outputs,
              treasury_in_datum: treasury_in_datum,
              treasury_in_value: treasury_in_value,
              mint_value: mint_value,
              treasury_hash: treasury_hash,
            )
        }
      CancelLBE(reason) -> {
        // finding exactly 1 Legit Treasury Output
        let treasury_output =
          validation.get_treasury_output(
            outputs,
            factory_policy_id,
            treasury_hash,
          )
        validate_cancel_lbe(
          treasury_in_datum: treasury_in_datum,
          treasury_in_value: treasury_in_value,
          treasury_output: treasury_output,
          ref_inputs: reference_inputs,
          inputs: inputs,
          extra_signatories: extra_signatories,
          end_valid_time_range: end_valid_time_range,
          cancel_reason: reason,
          mint_value: mint_value,
        )
      }
    }
  }
}

