        |> value.add(authen_policy_id, utils.pool_auth_asset_name, 1)
    and {
      // stake_credential must be the same with configured credential
      pool_stake_credential == pool_batching_stake_credential,
      // asset_a and asset_b must be the same with Factory Redeemer
      pool_datum_asset_a == asset_a,
      pool_datum_asset_b == asset_b,
      // Total Liquidity in PoolDatum must be sqrt(amount_a * amount_b)
      pool_datum_total_liquidity == total_liquidity,
      // Pool Reserve must be the same between datum and value 
      pool_datum_reserve_a == amount_a,
      pool_datum_reserve_b == amount_b,
      // Initial Pool Reserves must be positive and the liquditiy must be greater than the burn liquidity
      amount_a > 0,
      amount_b > 0,
      total_liquidity > utils.default_burn_liquidity,
      // base_fee_a_numerator and base_fee_b_numerator must be the same
      pool_datum_base_fee_a_numerator == pool_datum_base_fee_b_numerator,
      // base_fee_a_numerator and base_fee_b_numerator must be between **5** and **2000**
      pool_validation.validate_fee_percent(
        fee_num: pool_datum_base_fee_a_numerator,
        max_fee_num: utils.max_base_fee_numerator,
        min_fee_num: utils.min_base_fee_numerator,
      ),
      // Dynamic Fee is not allowed in Pool Creation 
      pool_allow_dynamic_fee == False,
      // fee_sharing_numerator_opt must be empty
      pool_datum_fee_sharing_numerator_opt == None,
      // Transaction must mint necessary assets
      value.from_minted_value(mint) == pool_validation.get_pool_creation_expected_mint(
        authen_policy_id: authen_policy_id,
        lp_asset_name: lp_asset_name,
      ),
      // Pool Value must only have necessary Token:
      // - Asset A 
      // - Asset B
      // - remaining LP Token (_MAX_INT64_ - _total_liquidity_)
      // - 1 Pool NFT Token
      // - 3 ADA (required ADA for an UTxO)
      expected_pool_out_value == pool_output_value,
    }
  }
}

use aiken/builtin
use aiken/collection/dict
use aiken/collection/list
use aiken/crypto.{blake2b_256}
use aiken/primitive/bytearray
use cardano/address.{Address, PaymentCredential, Script}
use cardano/assets.{PolicyId}
use cardano/transaction.{
  InlineDatum, Input, Output, OutputReference, Transaction,
} as tx

pub type Action {
  Mint(Int)
  Burn
}

pub type SpendTokenName =
  ByteArray

validator redeem(creator: ByteArray) {
  spend(
    // Each spend input checks for a token name matching the datum being burned
    datum: Option<SpendTokenName>,
    _r: Data,
    own_ref: OutputReference,
    transaction: Transaction,
  ) {
    let Transaction { inputs, mint, .. } = transaction

    expect Some(own_input) =
      list.find(inputs, fn(input) { input.output_reference == own_ref })

    let Input {
      output: Output { address: Address { payment_credential, .. }, .. },
      ..
    } = own_input

    expect Script(own_validator_hash) = payment_credential
    expect Some(datum) = datum

    (
      mint
        |> assets.quantity_of(own_validator_hash, datum)
    ) == -1
  }

  mint(rdmr: Action, policy_id: PolicyId, transaction: Transaction) {
    let Transaction { inputs, mint, extra_signatories, outputs, .. } =
      transaction

    let minted_assets =
      mint
        |> assets.tokens(policy_id)
        |> dict.to_pairs()

    when rdmr is {
      Mint(total) -> {
        expect [input, ..] = inputs

        // Base is created from serializing a utxo ref being spent. Thus this guarantees a unique base
        let base = builtin.serialise_data(input.output_reference)

        // Create a list of expected token names
        let expected_minted_token_names =
          create_expected_minted_nfts(base, total, [])

        // Check contract creator is a signer of this tx
        let signature_check =
          list.any(extra_signatories, fn(n) { creator == n })

        // Support multiple gift card creation by allowing a
        // 'number of tokens minted' == 'outputs with datum being token name'
        signature_check && check_mint_and_outputs(
          minted_assets,
          outputs,
          expected_minted_token_names,
          Script(policy_id),
        )
      }
      Burn ->
        list.all(
          minted_assets,
          fn(asset) {
            let Pair(_, amount) = asset
            amount == -1
          },
        )
    }
  }

  else(_) {
    fail
  }
}

fn insert(self: List<a>, e: a, compare: fn(a, a) -> Ordering) -> List<a> {
  when self is {
    [] -> [e]
    [x, ..xs] ->
      if compare(e, x) == Less {
        [e, ..self]
      } else {
        [x, ..insert(xs, e, compare)]
      }
  }
}

// Check each minted token name is in the expected list, has quantity of 1,
// and has a corresponding ouput with datum containing token name.
// Otherwise fail
fn check_mint_and_outputs(
  minted_assets: Pairs<ByteArray, Int>,
  outputs: List<Output>,
  expected_assets: List<ByteArray>,
  validator_cred: PaymentCredential,
) -> Bool {
  when minted_assets is {
    [] -> True
    [Pair(minted_asset_name, quantity), ..rest_assets] -> {
      expect
        list.any(
          expected_assets,
          fn(expected_asset) { expected_asset == minted_asset_name },
        )

      expect
        list.any(
          outputs,
          fn(output) {
            let Output { address, datum, .. } = output
            datum == InlineDatum(minted_asset_name) && address.payment_credential == validator_cred
          },
        )

      quantity == 1 && check_mint_and_outputs(
        rest_assets,
        outputs,
        expected_assets,
        validator_cred,
      )
    }
  }
}

fn create_expected_minted_nfts(
  base: ByteArray,
  counter: Int,
  accum: List<ByteArray>,
) -> List<ByteArray> {
  if counter == 0 {
    accum
  } else {
    let token_name = blake2b_256(bytearray.push(base, counter))

    let accum = [token_name, ..accum]

    create_expected_minted_nfts(base, counter - 1, accum)
  }
}

