}

fn validate_pool_authorization_method(method: PoolAuthorizationMethod) -> Bool {
  let hash =
    when method is {
      PAMSignature(h) -> h
      PAMSpendScript(h) -> h
      PAMWithdrawScript(h) -> h
    }
  builtin.length_of_bytearray(hash) == 28
}

