          ..
        },
        ..
      },
      ..
    } = global_setting_input
    expect ScriptCredential(global_setting_script_hash) =
      global_setting_payment_credential

    let GlobalSetting { admin, .. } = datum
    let global_setting_output = outputs |> builtin.head_list
    let Output {
